contract Will_maker {
    //address[] wills;
    //address owner;
    //uint256 give_away;
    //bool alive;
    //bytes32 secret;
    //address executer;

    constructor() public payable {

    }
    /*
    modifier onlyOwner() {
        require(msg.sender == owner, "you do no have access to this");
        _;
    }

    modifier afterDeath() {
        require(alive == false, "owner not dead yet");
        _;
